import { useEffect, useRef, useState } from "react";
import Snake from "../Snake/Snake";
import gsap from "gsap";
import s from "./Board.module.scss";

const Board = () => {
    const [snakeData, setSnakeData] = useState([
        [0, 0],
        [0, 10],
    ]);
    
    const timer = useRef(0);
    const direction = useRef('RIGHT');
    
    const moveSnake = () => {
        let newSnakeData = [...snakeData];
        let head = newSnakeData[newSnakeData.length - 1];
        //console.log(head); //position de la tete avant changement
        
        switch (direction.current) {
            case 'UP':
            console.log("UP");
            head = [head[0], head[1] - 10];
            break;
            case 'DOWN':
            console.log("DOWN");
            head = [head[0], head[1] + 10];
            break;
            case 'LEFT':
            console.log("LEFT");
            head = [head[0] + 10, head[1]];
            break;
            case 'RIGHT':
            console.log("RIGHT");
            head = [head[0] + 10, head[1]];
            break;
            
            default:
            break;
        }

        newSnakeData.push(head);

        console.log(head); //position de la tete après changement
    };
    
    const onKeyDown = (e) => {
        //console.log(e);
        
        switch (e.keyCode) {
            case 90:
            //console.log("going up");
            direction.current = "UP";
            //Going up
            break;
            case 83:
            direction.current = "DOWN";
            //Going down
            break;
            case 81:
            direction.current = "LEFT";
            //Going left
            break;
            case 68:
            direction.current = "RIGHT";
            //Going right
            break;
            
            case 38:
            direction.current = "UP";
            //Going up
            break;
            case 40:
            direction.current = "DOWN";
            //Going down
            break;
            case 37:
            direction.current = "LEFT";
            //Going left
            break;
            case 39:
            direction.current = "RIGHT";
            //Going right
            break;
            
            default:
            break;
        }
    };
    
    const gameLoop = (time, deltaTime, frame) => {
        //console.log(time, deltaTime, frame);
        timer.current += deltaTime;
        
        if (timer.current > 500) {
            //console.log("move snake");
            timer.current = 0;
            moveSnake();
        }
    };
    
    useEffect(() => {
        window.addEventListener("keydown",onKeyDown);
        gsap.ticker.add(gameLoop);
    }, []);
    
    return (
        <div className={s.board}>
        <Snake data={snakeData} />
        </div>
    );
};

export default Board;
