import { useEffect, useRef, useState } from "react";
import Snake from "../Snake/Snake";
import gsap from "gsap";
import s from "./Board.module.scss";
import Food from "../Food/Food";
import { generateRandomCoordinates } from "../../utils/utils";

const Board = () => {
    const [snakeData, setSnakeData] = useState([
        [0, 0],
        [10, 0],
    ]);
    
    const [foodArray, setFoodArray] = useState([]);
    const [gameOver, setGameOver] = useState(false);
    
    const timer = useRef(0);
    const foodTimer = useRef(0);
    const direction = useRef('RIGHT');
    const canChangeDirection = useRef(true);

    const gameIsOver = () => {
        gsap.ticker.remove(gameLoop);
    };
    
    const IsOutOfBorder = () => {
        let head = snakeData[snakeData.length - 1];
        //console.log(head);
        
        if (head[0] >= 500 || head[1] >= 500 || head[0] < 0 || head[1] < 0) {
            return true;
        }else{
            return false;
        }
    };
    
    const moveSnake = () => {
        let newSnakeData = [...snakeData];
        let head = newSnakeData[newSnakeData.length - 1];
        //console.log(head); //position de la tete avant changement
        
        switch (direction.current) {
            case 'UP':
            console.log("UP");
            head = [head[0], head[1] - 10];
            break;
            case 'DOWN':
            console.log("DOWN");
            head = [head[0], head[1] + 10];
            break;
            case 'LEFT':
            console.log("LEFT");
            head = [head[0] - 10, head[1]];
            break;
            case 'RIGHT':
            console.log("RIGHT");
            head = [head[0] + 10, head[1]];
            break;
            
            default:
            break;
        }
        
        newSnakeData.push(head);
        newSnakeData.shift();
        
        const outOfBorder = IsOutOfBorder();
        
        setSnakeData(newSnakeData);
        
        //console.log(head); //position de la tete après changement
    };
    
    const onKeyDown = (e) => {
        //console.log(e);
        if (canChangeDirection.current === false) return;
        canChangeDirection.current = false;
        
        switch (e.keyCode) {
            case 90:
            //console.log("going up");
            if(direction.current !== "DOWN") {
                direction.current = "UP";
            }
            //Going up
            break;
            case 83:
            if(direction.current !== "UP") {
                direction.current = "DOWN";
            }
            //Going down
            break;
            case 81:
            if(direction.current !== "RIGHT") {
                direction.current = "LEFT";
            }
            //Going left
            break;
            case 68:
            if(direction.current !== "LEFT") {
                direction.current = "RIGHT";
            }
            //Going right
            break;
            
            case 38:
            if(direction.current !== "DOWN") {
                direction.current = "UP";
            }
            //Going up
            break;
            case 40:
            if(direction.current !== "UP") {
                direction.current = "DOWN";
            }
            //Going down
            break;
            case 37:
            if(direction.current !== "RIGHT") {
                direction.current = "LEFT";
            }
            //Going left
            break;
            case 39:
            if(direction.current !== "LEFT") {
                direction.current = "RIGHT";
            }
            //Going right
            break;
            
            default:
            break;
        }
    };
    
    const addFood = () => {
        console.log("add food");
        //génération cooredonnées
        const coordinates = generateRandomCoordinates();
        
        //maj du state
        setFoodArray((oldFoodArray) => [...oldFoodArray, coordinates]);
    };
    
    const gameLoop = (time, deltaTime, frame) => {
        //console.log(time, deltaTime, frame);
        timer.current += deltaTime * 0.001;
        foodTimer.current += deltaTime * 0.001;
        //console.log(foodTimer.current); voir le timer de la food
        
        if (foodTimer.current > 3) {
            foodTimer.current = 0;
            //console.log("pop");
            return addFood();
        }
        
        if (timer.current > .5) {
            //console.log("move snake");
            timer.current = 0;
            moveSnake();
            canChangeDirection.current = true;
        }
    };
    
    useEffect(() => {
        window.addEventListener("keydown",onKeyDown);
        gsap.ticker.add(gameLoop);
        
        return () => {
            window.removeEventListener("keydown", onKeyDown);
            gsap.ticker.remove(gameLoop);
        };
    }, [snakeData]);
    
    return (
        <div className={s.board}>
        <Snake data={snakeData} />
        {foodArray.map((food, i) => (
            <Food />
        ))}
        </div>
    );
};

export default Board;
